import React, { Component, Fragment } from 'react';
import {
    Row, Col,
    Button,
    CardHeader,
    Container,
    Card,
    CardBody,
    CardFooter,
    UncontrolledButtonDropdown, DropdownItem, DropdownMenu, DropdownToggle, ModalHeader, ModalBody, ModalFooter, Form,
    FormGroup, Label,
    Input, FormText, CardTitle,
    Nav,
    NavItem,
    NavLink,
} from 'reactstrap';

import Rodal from 'rodal';
import DatePicker from 'react-datepicker';
import { VerticalTimeline, VerticalTimelineElement } from 'react-vertical-timeline-component';
import PerfectScrollbar from 'react-perfect-scrollbar';
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import CountUp from 'react-countup';
import ToolkitProvider, { CSVExport } from 'react-bootstrap-table2-toolkit';
import paginationFactory from 'react-bootstrap-table2-paginator';
import {
    Sparklines,
    SparklinesCurve
} from 'react-sparklines';

import Ionicon from 'react-ionicons';

import PageTitle from '../../../Layout/AppMain/PageTitle';

import {
    faAngleUp,
    faAngleDown,
    faCalendarAlt,
    faEllipsisH,
    faCheck,
    faTrashAlt,
    faBusinessTime,
    faTimes
} from '@fortawesome/free-solid-svg-icons';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import Select from 'react-select';

import moment from 'moment';
import { Redirect } from 'react-router-dom'
import { Bar } from 'react-chartjs-2';

const options = [
    { value: '1', label: 'Today' },
    { value: '2', label: 'Week' },
    { value: '3', label: 'Month' },
    { value: '4', label: 'Yearly' },
];
const yearoptions = [
    { value: '2019', label: '2019' },
    { value: '2020', label: '2020' },
];
const monthoptions = [
    { value: '01', label: 'Jan' },
    { value: '02', label: 'Feb' },
    { value: '03', label: 'Mar' },
    { value: '04', label: 'Apr' },
    { value: '05', label: 'May' },
    { value: '06', label: 'Jun' },
    { value: '07', label: 'Jul' },
    { value: '08', label: 'Aug' },
    { value: '09', label: 'Sep' },
    { value: '10', label: 'Oct' },
    { value: '11', label: 'Nov' },
    { value: '12', label: 'Dec' },
];



export default class CriticalAnalysis extends Component {
    constructor() {
        super();

        this.state = {
            head: "Critical Analysis",
            datalabels: ['Today'],
            datavalues: [4],
            selectedOption: null,
            yearselect: null,
            weekselect: null,
            monthselect:null,
            yearvisible: false,
            weeksvisible: false,
            monthvisible:false,
            monthoptions:null,
            weekoptions: [],
            days:[],
            userdetails:''
        };

    }

    componentDidMount() {
        var onload = { value: "1", label: "Today" }
        this.handleChange(onload)
        console.log('test', (new Date(moment('2019-01-3'))).toDateString(), moment().year(2018).weeks(), new Date())
        // var year = { value: "2019", label: "2019" }
        // this.yearChange(year)
         var userexist =  JSON.parse(localStorage.getItem('user_data'));
         this.setState({
            userdetails: userexist
        }, () => {
            this.callApi('today')
        });

    }

    callApi(keydata) {
        var data = {}
        data['key'] = keydata
        if(keydata === 'week' || keydata === 'month' || keydata === 'year') {
            data['days'] = this.state.days
        }
        data['vendor_id'] = this.state.userdetails.user_id
        fetch('/criticalVendorData', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        }).then((response) => response.json())
            .then((responseData) => {
                console.log('Vendor Graphical data', responseData)
                if (responseData.code == 200) {
                    this.setState({
                        datavalues: responseData.filterData
                    }, () => {
                        // this.dataresult();
                    });
                } else {

                }

            });
    }

    handleChange = (selectedOption) => {

        if (selectedOption.value == 1) {
            this.setState({
                yearvisible: false,
                weeksvisible: false,
                monthvisible:false,
                yearselect:null,

            }, () => {

                var dbl=['Today']
                this.setState({
                    datalabels:dbl
                }, () => {
                    this.callApi('today')
                })
            })
        }else if (selectedOption.value == 2) {
            this.setState({
                yearvisible: true,
                monthvisible:false,
                yearselect:null,

            })
        }else if (selectedOption.value == 3) {
            this.setState({
                yearvisible: true,
                weeksvisible:false,
                yearselect:null,

            })
        }else if (selectedOption.value == 4) {
            this.setState({
                yearvisible: true,
                weeksvisible:false,
                monthvisible:false,
                yearselect:null,

            })
        }

        this.setState({ selectedOption });
    };

    yearChange = (yearselect) => {

        this.setState({ yearselect });
        var weeks = moment(yearselect).weeksInYear()
        var weekoptions = [];
        var monthoptions =[];
        for (var i = 1; i <= weeks; i++) {
            weekoptions.push({ value: i, label: i });
        }
